import React, { useEffect, useState } from 'react';
import client from '../unleash/client';

const WelcomeBanner = () => {
  const [enabled, setEnabled] = useState(false);

  useEffect(() => {
    const checkToggle = async () => {
      const isEnabled = await client.isEnabled('showWelcomeBanner');
      setEnabled(isEnabled);
    };
    checkToggle();
  }, []);

  return enabled ? <div>ðŸŽ‰ Welcome to the app!</div> : null;
};